    --mono-font: 'Fira Code Retina', 'Menlo', 'DejaVu Sans Mono', 'Liberation Mono', 'Consolas', 'Ubuntu Mono', 'Courier New', 'andale mono', 'lucida console', monospace;
    --emoji-font: "font_name", 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', 'Noto Color Emoji';
    --white: #fff;
    --bb: #2c2c2c;
    --dark: #292929;
    --darken: #333;
    --darker: #424242;
    --light: #e6e6e6;
    --lighter: #aaa;
    --accent: #4ce19d;
    --shadow: rgba(0,0,0,0.208);
    --yellow: #f0c674;
    --orange: #d98245;
    --red: #a54242;
    --magenta: #b294bb;
    --violet: #85678f;
    --blue: #54738c;
    --cyan: #5e8d87;
    --green: #7b823a;
    --red-darken: #4e3030;
    --red-darker: #3b2c2c;
    overflow-x: hidden;
  }
  @supports (-moz-user-select: none) {
