sourceCode:
/* ==UserStyle==
@name           Digital Ocean Dark Theme
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Digital Ocean dark theme
@author         maxthyron
@preprocessor   stylus

@var select theme                 "Select Theme:               " ["Default", "Custom"]
@var color dark                   "Primary Background Color:   "  #202020
@var color darker                 "Secondary Background Color: "  #151515
@var color light                  "Primary Highlight Color     "  #303030
@var color lighten                "Secondary Highlight Color:  "  #3e3e3e
@var color text-color             "Text Color:                 "  #cecece
@var color code-text              "Code Color:                 "  #cecece
@var color accent                 "Accent Color:               "  #3d9676
@var color transparent-accent     "Transparent Accent Color:   "  #3d967629
@var color link                   "Links Color:                "  #3d9676
@var color special-link           "Special Links Color:        "  #aacfb6
@var color tag-text               "Tags Color:                 "  #fff
==/UserStyle== */

@-moz-document domain("digitalocean.com") {
	:root {
		if (theme == "Default") {
			--dark:                 #202020;
			--darker:               #151515;
			--light:                #303030;
			--lighten:              #3e3e3e;
			--text-color:           #cecece;
			--code-text:            #fff;
			--accent:               #3d9676;
			--transparent-accent:   #3d96760f;
			--link:                 #3d9676;
			--special-link:         #aacfb6;
			--tag-text:             #fff;
		} else {
			--dark:                 dark;
			--darker:               darker;
			--light:                light;
			--lighten:              lighten;
			--text-color:           text-color;
			--code-text:            code-text;
			--accent:               accent;
			--transparent-accent:   transparent-accent;
			--link:                 link;
			--special-link:         special-link;
			--tag-text: 			tag-text;
		}
	}

	/* Table contents and related content titles */
	.table-of-contents:not(.table-of-contents--empty)::before, .tutorial-single .featured-items-right-bar h3 {
		color: var(--text-color);
	}

	/* Right bar and bottom related tutorials */
	.related-content ul li, .featured-items-right-bar .featured-item-highlight {
		background: var(--lighten);
		border: 0px !important;
		box-shadow: none;
	}
	.featured-item-regular:hover {
		opacity: 0.7;
	}
	.related-content ul li:hover, .featured-items-right-bar .featured-item-highlight:hover {
		background: var(--darker);
	}

	/* Right bar list children bars*/
	.featured-items-right-bar > a:not(:last-child) > .featured-item-regular {
		border-bottom: 3px solid var(--lighten) !important;
	}

	/* Bottom related content text */
	.related-content h2, .related-content h5, .related-content ul li p {
		color: var(--accent);
	}

	/* Meta tags */
	.meta-section .tag {
		background: var(--accent);
		color: var(--tag-text) !important;
		border: 0px;
	}
	.meta-section .tag:hover {
		background: invert(var(--accent));
		color: var(--tag-text) !important;
		border: 0px;
	}

	/* Date, time, views */
	.meta-section {
		color: var(--accent) !important;
	}

	/* Version helper*/
	.versioned-tutorial-group-navigation__header { 
		background: var(--accent);
	}

	/* All blocks */
	.looking-for-an-answer .lfa--actions .lfa--action-box, .prismic-section.hero .prismic-highlight {
		background: var(--lighten);
		border: none;
	}

	/* All buttons */
	.prismic-section .cta, .response .log-in-notice a, a.load-more {
		background: var(--accent);
		color: var(--text-color) !important;
	}

	/* Was it helpful */
	.hsb--content .hsb--button {
		color: var(--accent);
		border: 1px solid var(--accent);
	}
	.hsb--content .hsb--button:hover {
		color: var(--text);
		background: var(--accent);
		border: 1px solid var(--accent);
	}
	.hsb--content, .hsb--content .hsb--do {
		background: var(--light);
		border: none !important;
	}


	/* All links and special titles */
	a, .expander, .names, .prismic-section .copy h2, .hsb--content .hsb--question {
		color: var(--accent) !important;
	}

	/* Search bars */
	.search_button {
		background: var(--dark) !important;
		color: var(--text-color) !important;
		border: none !important;
	}
	/* Keyboard shortcut icon */
	kbd {
		background: var(--dark) !important;
		color: var(--text-color) !important;
		border: none !important;
		box-shadow: 0 0 0 1px var(--text-color) inset !important;
	}

	/* Important links inside table of contents (current topic) */
	.table-of-contents ul li.tocify-focus a {
		color: var(--special-link) !important;
	}

	/* Active links bottom border */
	a.active {
		border-bottom: 2px solid var(--accent) !important;
	}

	/* Author link */
	.component-collaborators-content .collaborators-byline-data .names a, .content-body a {
		color: var(--accent) !important;
		border-bottom: 1px solid var(--accent);
	}

	/* Notes */
	.note {
		background: var(--lighten);
		color: var(--text-color);
	}

	/* Labels */
	.code-label {
		background: var(--accent);
		color: var(--text-color);
	}

	/* Background and scrollbar colors for all pages */
	body, .prismic-section.two_column, .prismic-section.standard_gray, .prismic-section.hero, footer section .panel {
		background: var(--dark) !important;
		scrollbar-color: var(--lighten) var(--darker);
		color: var(--accent) !important;
	}

	/* Scroll up button */
	#scrollUp:hover {
		background: var(--darker);
	}
	#scrollUp {
		background: var(--dark);
	}

	/* Code snippets*/
	pre code {
		background: var(--darker) !important;
		color: var(--code-text) !important;
	}

	/* Code refrences */
	code {
		color: var(--accent);
		background: var(--light);
	}

	/* Highlights inside code */
	span.highlight, .secondary-code-label {
		color: var(--accent);
	}

	/* Code snippet background */
	pre {
		background-color: var(--darker);
	}

	pre .prefixed, pre code {
		font-size: 12px;
		font-family: "Fira Code Retina";
	}


	/* Footer in the end*/
	footer {
		background: var(--light) !important;
	}

	/* Header */
	.do_nav .utility, .do_nav .primary, .do_nav .mini, .do_nav header, .do_nav .mega, .do_nav .mega ul[role="menu"] li a strong, .modal-content, .mini-modal h1 {
		background: var(--light) !important;
		color: var(--accent) !important;
		border: 0px !important;
	}
	/* Language menu */
	.content-translations ul li.current, .content-translations ul li.current .icon-check, .content-translations ul li.current .icon-check-light {
		color: var(--special-link);
	}
	.content-translations ul li, .modal-header {
		border-bottom: 3px solid var(--lighten);
	}
	.content-translations ul {
		border-top: 3px solid var(--lighten);
	}
	.mini-modal {
		background-color: var(--transparent-accent);
		color: #666;
		top: 0 !important;
	}
	/* Delete legacy header border color */
	.tutorial-single .legacy-header {
		border-bottom: none;
	}

	/* Table of contents to the left and related bar to the right */
	.table-of-contents.has-init, .featured-items-right-bar {
		background: var(--light);
		outline: 10px solid var(--light);
	}

	/* Right bar items */
	.featured-item-highlight, .featured-item-regular, .featured-item-category { 
		color: var(--accent) !important;
	}

	/* Titles */
	.content .content-title, .content-body h2, .content-body h3 {
		color: var(--text-color);
	}

	/* Content text */
	.tutorial-single .tutorial-content {
		color: var(--text-color);
	}

	/* Footer with author, contributors and helpfullness */
	.tutorial-single .tutorial-footer .tutorial-footer-details {
		border: none;
		background: var(--dark) !important;
	}

	/* Footer with other tutorials */
	.tutorial-single .tutorial-footer .section-content {
		background: var(--light);
	}

	/* Footer blocks with other info */

	footer section .panel .heading {
		color: var(--accent);
	}

	/* Contributors */
	.tutorial-single .tutorial-footer .tutorial-contributors {
		background: var(--dark);
		border-top: 4px solid var(--lighten);
	}

	/* Looking for an answer */
	.looking-for-an-answer .section-content {
		background: var(--dark) !important;
	}
	.looking-for-an-answer .section-content span {
		background: none !important;
		border-radius: 5px;
	}
	.looking-for-an-answer .lfa--actions .lfa--action-box {
		border: none;
	}
	.looking-for-an-answer h4 {
		color: var(--accent);
	}
	.looking-for-an-answer .lfa--actions .lfa--action-box:hover {
		background: var(--darker) !important;
		box-shadow: none;
	}

	/* Comments */
	.commentable .comments-header h4 {
		color: var(--accent);
	}
	.answers-container .meltdown_bar, .project-single .meltdown_bar, .tutorial-single .meltdown_bar, .meltdown_editor-wrap .meltdown_editor-deco textarea, .answers-container .meltdown_wrap, .project-single .meltdown_wrap, .tutorial-single .meltdown_wrap {
		background: var(--lighten);
		border: none;
	}

	.meltdown_bar ul.meltdown_controls li {
		background: var(--accent);
		background-repeat: no-repeat;
	}
	/* Comments */
	.response-list * {
		background: var(--light);
		color: var(--text-color) !important;
		border-radius: 5px;
		border: 2px solid var(--light);
	}
	/* Load more comments button */
	a.load-more:hover {
		background: var(--accent);
	}

	/* License */
	.tutorial-single .creative-commons {
		background: var(--dark);
		border-top: 3px solid var(--light);
		padding-top: 20px;
	}
}

